import React, { useState } from 'react';
import { Shield, AlertTriangle, CheckCircle, Clock } from 'lucide-react';

const WebRiskDemo = () => {
  // State for both scan and submission features
  const [activeTab, setActiveTab] = useState('scan');
  const [url, setUrl] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [submissions, setSubmissions] = useState([]);
  const [submissionForm, setSubmissionForm] = useState({
    url: '',
    evidence: '',
    submissionType: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Existing scan functionality
  const handleScan = async () => {
    setLoading(true);
    try {
      console.log('Sending request to scan URL:', url);
      const response = await fetch('/api/scan', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url })
      });

      console.log('Response status:', response.status);
      const data = await response.json();
      console.log('Response data:', data);

      const isSafe = data.scores.every(score => score.confidenceLevel === "SAFE");

      setResults([{
        id: Date.now(),
        url,
        status: isSafe ? 'safe' : 'threat',
        timestamp: new Date().toISOString(),
        scores: data.scores
      }, ...results]);
    } catch (error) {
      console.error('Error during scan:', error);
      setResults([{
        id: Date.now(),
        url,
        status: 'error',
        timestamp: new Date().toISOString(),
        details: 'Failed to scan URL'
      }, ...results]);
    }

    setLoading(false);
    setUrl('');
  };

  // New submission functionality
  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('/api/submit', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          url: submissionForm.url,
          evidence: submissionForm.evidence,
          type: submissionForm.submissionType
        })
      });

      const data = await response.json();

      if (data.operation) {
        setSubmissions([{
          id: Date.now(),
          url: submissionForm.url,
          operation: data.operation,
          timestamp: data.timestamp,
          evidence: submissionForm.evidence,
          type: submissionForm.submissionType,
          status: 'PENDING'
        }, ...submissions]);

        // Reset form
        setSubmissionForm({
          url: '',
          evidence: '',
          submissionType: ''
        });
      }
    } catch (error) {
      console.error('Submission error:', error);
    }

    setIsSubmitting(false);
  };

  // Helper functions
  const getConfidenceLevelStyle = (level) => {
    switch (level) {
      case 'SAFE':
      case 'LOW':
        return 'bg-green-100 text-green-700';
      case 'MEDIUM':
        return 'bg-orange-100 text-orange-700';
      case 'HIGH':
      case 'EXTREMELY_HIGH':
        return 'bg-red-100 text-red-700';
      default:
        return 'bg-gray-100 text-gray-700';
    }
  };

  const getSubmissionStatusStyle = (status) => {
    switch (status) {
      case 'SUCCEEDED':
        return {
          icon: <CheckCircle className="h-5 w-5 text-green-500" />,
          text: 'Added to blocklist',
          className: 'bg-green-100 text-green-700'
        };
      case 'CLOSED':
        return {
          icon: <AlertTriangle className="h-5 w-5 text-gray-500" />,
          text: 'Not added',
          className: 'bg-gray-100 text-gray-700'
        };
      default:
        return {
          icon: <Clock className="h-5 w-5 text-blue-500" />,
          text: 'Processing',
          className: 'bg-blue-100 text-blue-700'
        };
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="bg-white rounded-lg shadow-lg">
        <div className="border-b">
          <div className="flex gap-2 px-6 pt-4">
            <button
              onClick={() => setActiveTab('scan')}
              className={`px-4 py-2 rounded-t-lg ${
                activeTab === 'scan'
                  ? 'bg-white border-b-2 border-blue-600 text-blue-600 font-semibold'
                  : 'bg-gray-50 text-gray-600 hover:bg-gray-100'
              }`}
            >
              Scan URLs
            </button>
            <button
              onClick={() => setActiveTab('submit')}
              className={`px-4 py-2 rounded-t-lg ${
                activeTab === 'submit'
                  ? 'bg-white border-b-2 border-blue-600 text-blue-600 font-semibold'
                  : 'bg-gray-50 text-gray-600 hover:bg-gray-100'
              }`}
            >
              Submit URLs
            </button>
          </div>
        </div>

        {activeTab === 'scan' ? (
          // Scan Tab Content
          <div className="p-6">
            <div className="flex items-center gap-2 mb-6">
              <Shield className="h-6 w-6 text-blue-600" />
              <h1 className="text-2xl font-bold">Google Cloud Web Risk Demo</h1>
            </div>

            <div className="flex gap-4 mb-8">
              <input
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="Enter URL to scan..."
                className="flex-1 px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button 
                onClick={handleScan}
                disabled={!url || loading}
                className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 focus:outline-none focus:ring-2 focus:ring-blue-500"
              >
                {loading ? 'Scanning...' : 'Scan URL'}
              </button>
            </div>

            <div className="space-y-4">
              {/* Existing scan results display */}
              {results.map((result) => (
                <div key={result.id} className="bg-gray-50 rounded-lg p-4 border">
                  {/* ... (keep existing result display code) ... */}
                </div>
              ))}

              {results.length === 0 && (
                <div className="text-center py-8 text-gray-500">
                  No scans performed yet. Enter a URL above to begin.
                </div>
              )}
            </div>
          </div>
        ) : (
          // Submit Tab Content
          <div className="p-6">
            <div className="bg-blue-50 p-4 rounded-lg mb-6">
              <h3 className="flex items-center gap-2 font-medium text-blue-800 mb-2">
                <Shield className="h-5 w-5" />
                Submission Guidelines
              </h3>
              <div className="text-sm text-blue-700 space-y-2">
                <p>Only submit URLs that clearly violate Safe Browsing policies:</p>
                <ul className="list-disc pl-5 space-y-1">
                  <li>Social engineering/phishing sites mimicking legitimate brands</li>
                  <li>Sites distributing known malware</li>
                </ul>
                <p className="italic">Note: Submissions may take up to 24 hours to process.</p>
              </div>
            </div>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Submission Type
                </label>
                <select
                  value={submissionForm.submissionType}
                  onChange={(e) => setSubmissionForm({
                    ...submissionForm,
                    submissionType: e.target.value
                  })}
                  className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500"
                  required
                >
                  <option value="">Select type...</option>
                  <option value="phishing">Phishing/Social Engineering</option>
                  <option value="malware">Malware Distribution</option>
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  URL to Submit
                </label>
                <input
                  type="text"
                  value={submissionForm.url}
                  onChange={(e) => setSubmissionForm({
                    ...submissionForm,
                    url: e.target.value
                  })}
                  className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500"
                  placeholder="Enter suspicious URL..."
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Evidence of Violation
                </label>
                <textarea
                  value={submissionForm.evidence}
                  onChange={(e) => setSubmissionForm({
                    ...submissionForm,
                    evidence: e.target.value
                  })}
                  className="w-full px-4 py-2 border rounded-md focus:ring-2 focus:ring-blue-500"
                  placeholder="Describe how this URL violates policies..."
                  rows={4}
                  required
                />
              </div>

              <button
                type="submit"
                disabled={isSubmitting}
                className="w-full px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
              >
                {isSubmitting ? 'Submitting...' : 'Submit URL'}
              </button>
            </form>

            {/* Submission History */}
            <div className="mt-8">
              <h3 className="text-lg font-medium mb-4">Submission History</h3>
              <div className="space-y-4">
                {submissions.map((submission) => {
                  const status = getSubmissionStatusStyle(submission.status);
                  return (
                    <div key={submission.id} className="bg-gray-50 rounded-lg p-4 border">
                      <div className="flex justify-between items-start">
                        <div className="space-y-2">
                          <div className="font-medium break-all">{submission.url}</div>
                          <div className="text-sm text-gray-500">
                            Submitted: {new Date(submission.timestamp).toLocaleString()}
                          </div>
                          <div className="text-sm">
                            Type: {submission.type}
                          </div>
                        </div>
                        <div className={`flex items-center gap-2 px-3 py-1 rounded-full ${status.className}`}>
                          {status.icon}
                          <span className="text-sm">{status.text}</span>
                        </div>
                      </div>
                    </div>
                  );
                })}

                {submissions.length === 0 && (
                  <div className="text-center py-8 text-gray-500">
                    No submissions yet. Submit a URL above to get started.
                  </div>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default WebRiskDemo;
