import React, { useState } from 'react';
import { Shield, AlertTriangle, CheckCircle } from 'lucide-react';

const WebRiskDemo = () => {
  const [url, setUrl] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  // This function handles the URL scanning process
  const handleSubmit = async () => {
    setLoading(true);
    try {
      // Log the URL we're about to scan
      console.log('Sending request to scan URL:', url);

      // Make the API request to our backend
      const response = await fetch('/api/scan', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url })
      });

      // Log the response we get back
      console.log('Response status:', response.status);
      const data = await response.json();
      console.log('Response data:', data);

      // Add the new result to our list
      setResults([{
        id: Date.now(),
        url,
        status: data.status,
        timestamp: new Date().toISOString(),
        threatTypes: data.threats,
        details: data.error || 'Scan completed successfully'
      }, ...results]);
    } catch (error) {
      // Log and handle any errors that occur
      console.error('Error during scan:', error);
      setResults([{
        id: Date.now(),
        url,
        status: 'error',
        timestamp: new Date().toISOString(),
        details: 'Failed to scan URL'
      }, ...results]);
    }

    setLoading(false);
    setUrl('');
  };

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div className="bg-white rounded-lg shadow-lg p-6">
        <div className="flex items-center gap-2 mb-6">
          <Shield className="h-6 w-6 text-blue-600" />
          <h1 className="text-2xl font-bold">Google Cloud Web Risk Demo</h1>
        </div>

        <div className="flex gap-4 mb-8">
          <input
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            placeholder="Enter URL to scan..."
            className="flex-1 px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button 
            onClick={handleSubmit}
            disabled={!url || loading}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {loading ? 'Scanning...' : 'Scan URL'}
          </button>
        </div>

        <div className="space-y-4">
          {results.map((result) => (
            <div key={result.id} className="bg-gray-50 rounded-lg p-4 border">
              <div className="flex items-start justify-between">
                <div className="space-y-2">
                  <div className="font-medium break-all">{result.url}</div>
                  <div className="text-sm text-gray-500">
                    Scanned at: {new Date(result.timestamp).toLocaleString()}
                  </div>
                  {result.threatTypes?.length > 0 && (
                    <div className="flex gap-2">
                      {result.threatTypes.map((threat) => (
                        <span
                          key={threat}
                          className="px-2 py-1 text-xs rounded bg-red-100 text-red-700"
                        >
                          {threat}
                        </span>
                      ))}
                    </div>
                  )}
                </div>
                <div className="flex items-center gap-2">
                  {result.status === 'safe' ? (
                    <CheckCircle className="h-6 w-6 text-green-500" />
                  ) : (
                    <AlertTriangle className="h-6 w-6 text-red-500" />
                  )}
                </div>
              </div>
            </div>
          ))}

          {results.length === 0 && (
            <div className="text-center py-8 text-gray-500">
              No scans performed yet. Enter a URL above to begin.
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WebRiskDemo;
